const selectedCategory = (category: string, selected: string) =>
  category === selected

export const Categories: React.FC<CategoriesProps> = ({
  selected,
  onSelect,
}) => (
  <Box p={3} display="flex" flexDirection="row" {...ReverseElementColors}>
    <Box width="40%">
      <Text color="white" block type="primary">
        {selected}
      </Text>
    </Box>
    <Box
      width="60%"
      display="flex"
      flexDirection="row"
      justifyContent="space-between"
    >
      {categories.map((category, i) => {
        const color = selectedCategory(category, selected)
          ? theme.black
          : theme.white
        const backgroundColor = selectedCategory(category, selected)
          ? theme.white
          : theme.dark
        return (
          <a
            key={i}
            onClick={() => {
              onSelect && onSelect(category)
            }}
          >
            <CategoryIcon backgroundColor={backgroundColor}>
              {svgs[category](color)}
            </CategoryIcon>
          </a>
        )
      })}
    </Box>
  </Box>
)

export const CategoryIcon = styled.div<any>`
  border-radius: 50%;
  min-width: 42px;
  height: 42px;
  display: inline-block;
  padding: 10px;
  background-color: ${p => p.backgroundColor};
`
